example.programlisting:
global reads
probe vfs.read
{ 
	reads[execname()] ++
}
probe timer.s(3)
{
	foreach (count in reads)
		printf("%s : %d \n", count, reads[count])
}

section.screen:
probe timer.s(3)
{
	foreach (count in reads- limit 10)
		printf("%s : %d \n", count, reads[count])
}

example.programlisting:
global reads
probe vfs.read
{ 
	reads[execname()] ++
}
probe timer.s(3)
{
	foreach (count in reads)
		printf("%s : %d \n", count, reads[count])
	delete reads	
}

note.screen:
global reads, totalreads

probe kernel.function("vfs_read")
{
	reads[execname()] ++
	totalreads[execname()] ++
}

probe timer.s(2)
{
	printf("=======\n")
	foreach (count in reads+) 
		printf("%s : %d \n", count, reads[count])
	delete reads

}
probe end
{
	printf("TOTALS\n")
	foreach (total in totalreads+)
		printf("%s : %d \n", total, totalreads[total])
}

example.programlisting:
global reads
probe vfs.read
{
    reads[execname()] ++
}

probe timer.s(2)
{
    printf("=======\n")
    foreach (count in reads-)
        if (reads[count] >= 1024)
            printf("%s : %dkB \n", count, reads[count]/1024)
        else
            printf("%s : %dB \n", count, reads[count])
}

global reads

probe vfs.read
{
	reads[execname()] ++
}

probe timer.s(2)
{
	printf("=======\n")
	foreach (count in reads+) 
		printf("%s : %d \n", count, reads[count])
	if(["stapio"] in reads)
	{
		printf("stapio read detected, exiting\n")
		exit()
	}
}

global reads	
probe vfs.read
{
reads[execname()] <<< count
}
