example.programlisting:
probe syscall.open
{
  printf ("%s(%d) open\n", execname(), pid())
}

probe kernel.function("*@net/socket.c") 
{
  printf ("%s -> %s\n", thread_indent(1), probefunc())
}
probe kernel.function("*@net/socket.c").return 
{
  printf ("%s <- %s\n", thread_indent(-1), probefunc())
}

probe syscall.* {
  if (pid() == target())
    printf("%s/n", name)
}
